defmodule Librecov.Fixtures do
  @coverages_path Path.join(__DIR__, "../fixtures/dummy-coverages.json")
  @coverages @coverages_path |> File.read!() |> Jason.decode!()

  def dummy_coverages do
    @coverages
  end

  def dummy_coverage do
    Enum.at(dummy_coverages(), 0)
  end
end

defmodule :map do
  @behaviour Ecto.Type

  def type, do: :json

  def cast(any), do: {:ok, any}
  def load(value), do: Jason.decode(value)
  def dump(value) when is_binary(value), do: {:ok, value}
  def dump(value), do: Jason.encode(value)
  def equal?(value, other), do: Jason.encode(value) == Jason.encode(other)
end
